//facilita a pesquisa da cor e tonalidade(tonalidades escuras: 600,700,800,900; tonalidades claras: 50,100,200,300,400)
export default function Cor(color, colorTonality = 500) {
  switch (color) {
    case 'red':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorRed)
        : colors.colorRed.mainColor
    case 'blue':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorBlue)
        : colors.colorBlue.mainColor
    case 'grey':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorGrey)
        : colors.colorGrey.mainColor
    case 'green':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorGreen)
        : colors.colorGreen.mainColor
    case 'pink':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorPink)
        : colors.colorPink.mainColor
    case 'purple':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorPurple)
        : colors.colorPurple.mainColor
    case 'deep-purple':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorDeepPurple)
        : colors.colorDeepPurple.mainColor
    case 'indigo':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorIndigo)
        : colors.colorIndigo.mainColor
    case 'light-blue':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorLightBlue)
        : colors.colorLightBlue.mainColor
    case 'cyan':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorCyan)
        : colors.colorCyan.mainColor
    case 'teal':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorTeal)
        : colors.colorTeal.mainColor
    case 'light-green':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorLightGreen)
        : colors.colorLightGreen.mainColor
    case 'lime':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorLime)
        : colors.colorLime.mainColor
    case 'yellow':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorYellow)
        : colors.colorYellow.mainColor
    case 'amber':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorAmber)
        : colors.colorAmber.mainColor
    case 'orange':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorOrange)
        : colors.colorOrange.mainColor
    case 'deep-orange':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorDeepOrange)
        : colors.colorDeepOrange.mainColor
    case 'brown':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorBrown)
        : colors.colorBrown.mainColor
    case 'blue-grey':
      return colorTonality >= 50 && colorTonality != 500
        ? tonality(colorTonality, colors.colorBlueGrey)
        : colors.colorBlueGrey.mainColor
    case 'facebook':
      return colors.facebookColor
    case 'google':
      return colors.googleColor
    case 'flix':
      return colors.flixColor
    case 'black':
      return colors.colorBlack
    case 'white':
      return colors.colorWhite
    default:
      return console.log('Digite uma cor v√°lida!')
  }
}

function tonality(colorTonality, color) {
  switch (colorTonality) {
    case 900:
      return color.darknessColor900
    case 800:
      return color.darknessColor800
    case 700:
      return color.darknessColor700
    case 600:
      return color.darknessColor600
    case 400:
      return color.lightnessColor400
    case 300:
      return color.lightnessColor300
    case 200:
      return color.lightnessColor200
    case 100:
      return color.lightnessColor100
    case 50:
      return color.lightnessColor50
    default:
      return color.mainColor
  }
}
